def update_display():
    """
    Update various display elements on the GUI.
    
    This function updates the temperature setpoint, room temperature, heater status,
    and energy consumption (kWh) information on the GUI. It also updates the IP 
    address and the state of a toggle graphic.
    
    Parameters in controllersParameters:
        - 'temperature_setpoint': Target temperature set by the user
        - 'room_temperature': Current room temperature
        - 'heater_status': Status of the heater (On/Off)
        - 'totalkWh': Total energy consumption in kWh
        - 'todaykWh': Energy consumption of the current day in kWh
        - 'yesterdaykWh': Energy consumption of the previous day in kWh
        - 'plug_ip': IP address of the plug
    """
    # Update Temperature Display on GUI
    window['-SETPOINT-'].update(f"{controllersParameters['temperature_setpoint']:.1f}")
    window['-TEMP-'].update(f"{controllersParameters['room_temperature']:.1f}")
    
    # Update Heater Status Display on GUI
    window['-STATUS-'].update(controllersParameters['heater_status'])
    
    # Update kWh Display on GUI
    window['-TOTAL-'].update(f"{controllersParameters['totalkWh']: 3.2f}")
    window['-TODAY-'].update(f"{controllersParameters['todaykWh']: 3.2f}")
    window['-YESTERDAY-'].update(f"{controllersParameters['yesterdaykWh']: 3.2f}")
    
    # Update IP Address Display on GUI
    window['-IP-'].update(controllersParameters['plug_ip'])
    
    # Update Toggle Graphic Display on GUI
    window['-TOGGLE-GRAPHIC-'].update(image_data=toggle_btn_off if graphic_off else toggle_btn_on)


def send_Message(msg):
    """
    Sends an MQTT message to a specified topic.

    Args:
        msg (str): The message to be sent.

    Returns:
        None.
    """
    topic = TOPIC + SUB_TOPIC_SUPERVISOR
    # Publish the message to the MQTT broker
    result = client.publish(topic, msg)
    # Check if the message was successfully sent
    status = result[0]
    if status == 0:
        print(f"Sent `{msg}` to topic `{topic}`")
    else:
        print(f"Failed to send message to topic {topic}")

def append_to_csv(data_dict, csv_file):
    """
    Appends a dictionary to a CSV file. If the file does not exist, it is created with the
    headers from the dictionary keys.

    Parameters:
    - data_dict (dict): The dictionary to append to the CSV file
    - csv_file (str): The name of the CSV file to append to
    """
    # Define the headers for the CSV file
    headers = list(data_dict.keys())

    # Check if the file exists and create it if it doesn't
    try:
        with open(csv_file, mode="x", newline="") as f:
            writer = csv.DictWriter(f, fieldnames=headers)
            writer.writeheader()
    except FileExistsError:
        pass

    # Append the dictionary to the CSV file
    with open(csv_file, mode="a", newline="") as f:
        writer = csv.DictWriter(f, fieldnames=headers)
        writer.writerow(data_dict)

def on_message(client, userdata, message):
    """
    Callback function for when a message is received.
    Updates global variables based on the received message.

    Args:
        client: The MQTT client.
        userdata: User-defined data that is passed as the "userdata" parameter of the connect() function.
        message: The received message.

    Returns:
        None.
    """

    # Use global keyword to access global variables
    global readingTaken, controllersParameters, graphic_off, parametersReceived, display_update_required

    # Print the topic of the received message
    print(message.topic)

    # Convert the received message to string
    receivedMessage = message.payload.decode("utf-8")

    if 'RoomData' in message.topic:
        # Convert the received string to dictionary
        controllersParameters = json.loads(receivedMessage)

        # Check if heating is selected or not
        if controllersParameters['heating_selected'] == 'Yes':
            graphic_off = False
        else:
            graphic_off = True

        # # Request an update of the GUI display with the latest controller parameters
        # display_update_required = True
        # update_display()

        # Send custom event to the main thread for GUI update
        window.write_event_value(UPDATE_EVENT, None)

        # Set the flag to indicate that parameters have been received
        parametersReceived = True

        # Save the received parameters to a CSV file
        csv_file = f"{controllersParameters['room_name']}.csv"
        append_to_csv(controllersParameters, csv_file)

def send_mqtt_message():
    """
    Function that sends an MQTT message every 60 seconds to request the parameters
    
    """
    while not stop_mqtt_thread.is_set():
        time.sleep(PARAMETER_LOGGING_TIME_INTERVAL)  # Wait for PARAMETER INTERVAL time seconds
        send_Message('ParameterRequest')  # Send message to request current parameters
        print("Times Parameter Request Sent")
    
def handle_update_event(value):
    """
    Handle a custom event by updating the GUI.

    Parameters:
    ----------
    value : any
        The value associated with the custom event.

    Returns:
    -------
    None

    Notes:
    -----
    This function should be called when a custom event is triggered, and it will update 
    the graphical user interface (GUI) accordingly. It assumes that the 'update_display()' 
    function is defined elsewhere and will be responsible for making any necessary changes 
    to the display.
    """
    # Perform GUI update here
    update_display()


