display_update_required = False

PARAMETER_LOGGING_TIME_INTERVAL = 60 # Time between parameter requests for logging (in seconds)

# Initialize dictionary to store hourly setpoints with default value of 20 degrees Celsius
hourlySetpoints = {f'{hour:02d}:00': 20.0 for hour in range(0, 24)}

