while True:  # Main event loop

    now = datetime.datetime.now()  # Get current datetime

    event, values = window.read()  # Read events from GUI

    print(event, values)  # Print event and values for debugging purposes

    # if display_update_required == True:
    #     update_display()
    #     display_update_required = False

    if event in (sg.WIN_CLOSED, 'Exit'):  # Exit program if window closes or Exit button is clicked
        break       

    elif event == '+' and parametersReceived:  # Increase temperature setpoint by 0.1 if + button is clicked and parameters have been received
        controllersParameters['temperature_setpoint'] = round(controllersParameters['temperature_setpoint'] + 0.1, 1)
        window['-SETPOINT-'].update(f"{controllersParameters['temperature_setpoint']:.1f}")
        send_Message(f"TS:{controllersParameters['temperature_setpoint']}")

    elif event == '-' and parametersReceived:  # Decrease temperature setpoint by 0.1 if - button is clicked and parameters have been received
        controllersParameters['temperature_setpoint'] = round(controllersParameters['temperature_setpoint'] - 0.1, 1)
        window['-SETPOINT-'].update(f"{controllersParameters['temperature_setpoint']:.1f}")
        send_Message("TS:" + str(controllersParameters['temperature_setpoint']))

    elif event == '-TOGGLE-GRAPHIC-'and parametersReceived:  # Toggle graphic on/off if graphical button is clicked and parameters have been received
        graphic_off = not graphic_off
        window['-TOGGLE-GRAPHIC-'].update(image_data=toggle_btn_off if graphic_off else toggle_btn_on)
        if graphic_off:
            controllersParameters['heating_selected'] = "No"
            send_Message("HS:" + controllersParameters['heating_selected'])
        else:
            controllersParameters['heating_selected'] = "Yes"
            send_Message("HS:" + controllersParameters['heating_selected'])

    elif '-SLIDER-' in event:  # If any slider is moved, update the corresponding hourly setpoint value and send updated dictionary to controller
        # Extract the hour from the event key
        hour = int(event.split('-')[2])
        # Update the dictionary value
        hourlySetpoints[f'{hour:02d}:00'] = values[event]
        # Update the GUI to display the new value
        window[event].update(hourlySetpoints[f'{hour:02d}:00'])
        print(hourlySetpoints)
        send_Message(json.dumps(hourlySetpoints))

    elif event == UPDATE_EVENT:
        # Handle custom event for GUI update
        handle_update_event(values)

    update_display()  # Update GUI display

window.close()  # Close GUI window and exit program
client.loop_stop()  # Stop MQTT client loop

# Set the event flag to stop the MQTT thread
stop_mqtt_thread.set()

# Stop the MQTT message sending thread and join it with the main thread
mqtt_thread.join()